<?php/**	The function is only at working time  .******//*class notification{	private $user_id;	 		public function __construct($user){	        $this->user_id = $user;		    }		public function count(){				$user = $this->user_id;		$query = borno_query("SELECT * FROM prefix_notify WHERE id=$user");		return mysqli_num_rows($query);		}		public function view(){				$user = $this->user_id;		$query = borno_query("SELECT * FROM prefix_notify WHERE id=$user");				 //display with style		 while($row = mysqli_fetch_array($query)){			if($row['type']=='commentnotify'){				if(								is_exists_content($row['post_for'])				and 				get_the_post($row['post_for'],'post_status')=='publish'				){				echo '<a href="'.get_the_option('site_address').'/content/'.$row['post_for'].'&ref=notify">New Comment Added in the post '.get_the_post($row['post_for'],'title').' </a>';						}						}			else if($row['type']=='notice'){				$msg = $row['message'];				echo "<span class='text-info'>$msg</span>";									}		 		 		 }				}}//$t = new notification(1);//echo $t->view();*/