<?php/**	Borno CMS*	This is the plugin loadable file****/require_once('plugindetector.php');require_once('theme-detector.php');require_once('upload_plugin.php');require_once('upload_theme.php');/* //	Get the plugin value form the option table and convert it to an array 	// */$plugins = get_the_option('plugin');$plugins =stripcslashes($plugins);;$plugins =strip_tags($plugins);;$plugins = explode(',',$plugins);	/*	*	Checking same element form array	*	*	*	*	@param array @filename	*	@return integer	*/function check_multiple_load($filename){	global $plugins ;	$i = 0 ;		foreach($plugins as $plugin){			if($plugin==$filename){			$i++;			}					}				return $i;	}if(user_can('manage_plugin') and isset($_GET['loadplugin']) and $_GET['loadplugin']=='false'){	//not load plugin}else{if(!is_admin()){	foreach($plugins as $plugin){		////opend file		if(check_multiple_load($plugin)==1){			if(!strpos($plugin,'./')){			if(file_exists('include/plugin/'.$plugin.'/plug.php')){				$trim_plugin= trim($plugin);				if(!empty($trim_plugin))				include_once('include/plugin/'.$plugin.'/plug.php');			}			else{				//echo 'not exists';			}			}		}	}}else{	foreach($plugins as $plugin){		if(check_multiple_load($plugin)==1){			if(!strpos($plugin,'./')){			if(file_exists('../include/plugin/'.$plugin.'/plug.php')){				$trim_plugin= trim($plugin);				if(!empty($trim_plugin))				include_once('../include/plugin/'.$plugin.'/plug.php');			}			else{				//echo 'not exists'.$plugin;			}			}		}		}}}